import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, Button, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useIsFocused } from "@react-navigation/native";
const TopicsListScreen = ({ navigation, route }) => {
  const [topics, setTopics] = useState([]);
  const listId = route.params?.listId;
  const focus = useIsFocused();
  useEffect(() => {
    loadTopics();
  }, [focus]);

  const loadTopics = async () => {
    try {
      const storedTopics = await AsyncStorage.getItem(`topics_${listId}`);

      if (storedTopics !== null) {
        setTopics(JSON.parse(storedTopics));
      }
    } catch (e) {
      console.error('Failed to load topics.', e);
    }
  };

 const navigateToEditTopic = (topic) => {
    navigation.navigate('CreateTopic', { listId, topic });
  };

  const deleteTopic = async (topicId) => {
    const updatedTopics = topics.filter(topic => topic.id !== topicId);
    await AsyncStorage.setItem(`topics_${listId}`, JSON.stringify(updatedTopics));
    setTopics(updatedTopics);
  };

  const confirmDelete = (topicId) => {
    Alert.alert(
      "Excluir Tópico",
      "Tem certeza que deseja excluir este tópico?",
      [
        { text: "Cancelar", style: "cancel" },
        { text: "Excluir", onPress: () => deleteTopic(topicId) }
      ]
    );
  };


  return (
    <View style={styles.container}>
      <FlatList
        data={topics}
        keyExtractor={item => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.topicItem}>
            <Text style={styles.topicName}>{item.name}</Text>
            <Text style={styles.topicDate}>Criado em: {new Date(item.created).toLocaleDateString()}</Text>
            <View style={styles.buttonsContainer}>
              <TouchableOpacity style={styles.button} onPress={() => navigateToEditTopic(item)}>
                <Text>Editar</Text>
              </TouchableOpacity>
              <TouchableOpacity style={styles.button} onPress={() => confirmDelete(item.id)}>
                <Text>Excluir</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}
      />
      <Button title="Criar Tópico" onPress={() => navigation.navigate('CreateTopic', { listId })} />
    </View>
  );
};

const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 20,
    },
    topicItem: {
      backgroundColor: '#fff',
      padding: 20,
      marginVertical: 8,
      borderRadius: 5,
    },
    topicName: {
      fontSize: 18,
    },
    topicDate: {
      fontSize: 14,
      color: 'gray',
    },
    buttonsContainer: {
      flexDirection: 'row',
      justifyContent: 'space-around',
      marginTop: 10,
    },
    button: {
      padding: 10,
      backgroundColor: '#ddd',
      borderRadius: 5,
    },
  });

export default TopicsListScreen;
