import React, { useState, useEffect } from 'react';
import { View, Text, Button, FlatList, StyleSheet, TouchableOpacity } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

const HomeScreen = ({ navigation }) => {
  const [lists, setLists] = useState([]);

  useEffect(() => {
    loadLists();
  }, []);

  const loadLists = async () => {
    try {
      const storedLists = await AsyncStorage.getItem('lists');
      if (storedLists !== null) {
        setLists(JSON.parse(storedLists));
      }
    } catch (e) {
      // Error reading value
      console.error('Failed to load lists.', e);
    }
  };

  const deleteList = async (id) => {
    const updatedLists = lists.filter(list => list.id !== id);
    setLists(updatedLists);
    await AsyncStorage.setItem('lists', JSON.stringify(updatedLists));
  };

  return (
    <View style={styles.container}>
      <FlatList
        data={lists.sort((a, b) => new Date(b.created) - new Date(a.created))}
        keyExtractor={item => item.id}
        renderItem={({ item }) => (
          <View style={styles.listItem}>
            <Text style={styles.listTitle}>{item.title}</Text>
            <Text style={styles.listDate}>Criado em: {item.created}</Text>
            <View style={styles.buttonsContainer}>
              <TouchableOpacity
                style={styles.button}
                onPress={() => navigation.navigate('CreateList', { list: item })}
              >
                <Text>Editar</Text>
              </TouchableOpacity>
              <TouchableOpacity
                style={styles.button}
                onPress={() => deleteList(item.id)}
              >
                <Text>Excluir</Text>
              </TouchableOpacity>
            </View>
          </View>
        )}
      />
      <Button
        title="Criar Lista"
        onPress={() => navigation.navigate('CreateList')}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  listItem: {
    backgroundColor: '#fff',
    padding: 20,
    marginVertical: 8,
    borderRadius: 5,
  },
  listTitle: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  listDate: {
    fontSize: 14,
    color: 'gray',
  },
  buttonsContainer: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    marginTop: 10,
  },
  button: {
    backgroundColor: '#ddd',
    padding: 10,
    borderRadius: 5,
  },
});

export default HomeScreen;
