import React, { useState, useEffect } from 'react';
import { View, Text, FlatList, StyleSheet, Button } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { useIsFocused } from "@react-navigation/native";
const TopicsListScreen = ({ navigation, route }) => {
  const [topics, setTopics] = useState([]);
  const listId = route.params?.listId;
  const focus = useIsFocused();
  useEffect(() => {
    const loadTopics = async () => {
      try {
        const storedTopics = await AsyncStorage.getItem(`topics_${listId}`);
        if (storedTopics !== null) {
          setTopics(JSON.parse(storedTopics));
          console.log(topics)
        }
      } catch (e) {
        console.error('Failed to load topics.', e);
      }
    };

    loadTopics();
  }, [focus]);

  const navigateToCreateTopic = () => {
    navigation.navigate('CreateTopic', { listId });
  };

  return (
    <View style={styles.container}>
      <FlatList
        data={topics}
        keyExtractor={item => item.id.toString()}
        renderItem={({ item }) => (
          <View style={styles.topicItem}>
            <Text style={styles.topicName}>{item.TOPICNAME}</Text>
          </View>
        )}
      />
      <Button title="Criar TÃ³pico" onPress={navigateToCreateTopic} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 20,
  },
  topicItem: {
    backgroundColor: '#fff',
    padding: 20,
    marginVertical: 8,
    borderRadius: 5,
  },
  topicName: {
    fontSize: 18,
  },
});

export default TopicsListScreen;
